import React from "react";
import { baseURL } from "../shared/baseUrl";
import { Card, CardImg, CardText, CardBody, CardTitle, Form, FormGroup, Label, Input } from "reactstrap";

export function RenderComments({comments}){
    const comment = comments.map( (item) =>{
        return(
            <div>
                <h6>{"id:"+ item.id +"- rating:" + item.rating }</h6>
                <p>{item.comment}</p>
                <p>{item.author} {item.date}</p>
            </div>
        );
    });
    return (
        <div className="col-12 col-sm-7">
            <h2>comments</h2>
            {comment}
        </div>
    );
}

class TestFetchComponent extends React.Component{
    constructor(props) {
        super(props);
        this.state={
            dishes:[],
            comments:[]
        }
        this.fetchDishes();
        this.fetchComments();
    }
    fetchDishes(){
        return fetch(baseURL + 'dishes')
        .then(response => response.json())
        .then(data => {
            this.setState( {dishes:data});
        });
    }
    fetchComments(){
        return fetch(baseURL + 'comments')
        .then(response => response.json())
        .then(data => {
            this.setState( {comments:data});
        });
    }
    render(){

        const content=this.state.dishes.map( (dish) =>{
            return (
                <div className="col-12 col-sm-8" key={dish.id}>
                    <Card>
                        <CardImg top src={baseURL+dish.image} alt={dish.name}/>
                        <CardBody>
                            <CardTitle>{dish.name}</CardTitle>
                            <CardText>{dish.description}</CardText>
                        </CardBody>
                    </Card>
                    <h4>comments</h4>
                    <RenderComments comments={this.state.comments.filter((comment) =>
                        comment.dishId === parseInt(dish.id,10))} />
                        <h4>your comment</h4>
                        <Form>
                            <FormGroup>
                                <Label for="author" > author:</Label>
                                <Input type='text' id="author" name="author" placeholder="author"
                                value={this.state.author} onChange={this.handleInputChange}/>
                            </FormGroup>
                        </Form>
                </div>
            );
        });
        return (
            <div className="col-12 col-sm-5">
                {content}
            </div>
        );
    }
}
export default TestFetchComponent;
